interface Store {
  feeds: NewsFeed[];
  currentPage: number;
}

interface News {
  readonly id: number;
  readonly time_ago: string;
  readonly title: string;
  readonly url: string;
  readonly user: string;
  readonly content: string;
}

interface NewsFeed extends News {
  readonly points: number;
  readonly comments_count: number;
  read?: boolean;
}

interface NewsDetail extends News {
  readonly comments: NewsComment[];
}

interface NewsComment extends News {
  readonly comments: NewsComment[];
  readonly level: number;
}

const NEWS_URL = 'https://api.hnpwa.com/v0/news/1.json';
const CONTENT_URL = 'https://api.hnpwa.com/v0/item/@id.json';
const container: HTMLElement | null = document.getElementById('root');
const store: Store = {
  currentPage: 1,
  feeds: [],
};

class Api {
  ajax: XMLHttpRequest;
  url: string;

  constructor(url: string) {
    this.ajax = new XMLHttpRequest();
    this.url = url;
  }

  getRequest<AjaxResponse>(): AjaxResponse {
    this.ajax.open('GET', this.url, false);
    this.ajax.send();

    return JSON.parse(this.ajax.response);
  }
}

class NewsFeedApi extends Api {
  getData(): NewsFeed[] {
    return this.getRequest<NewsFeed[]>();
  }
}

class NewsDetailApi extends Api { 
  getData(): NewsDetail {
    return this.getRequest<NewsDetail>();
  }
}

class View{
  template : string;
  renderTemplate : string;
  container : HTMLElement;
  htmlList : string[];
  constructor(containerId: string, template:string){
    const containerElement= document.getElementById(containerId);

    if(!containerElement){
      throw '최상위 컨테이너가 없어 ui를 진행하지 못합니다.'
    }
    this.container = containerElement;
    this.renderTemplate = template;
    this.template = template;
    this.htmlList = [];
  }

  updateView(): void {
    this.container.innerHTML= this.renderTemplate; 
    this.renderTemplate = this.template; // 초기 값으로 변환
  }
  addHtml(htmlString : string) : void{
    this.htmlList.push(htmlString);
  }
  getHtml(): string {
    const snapshot = this. htmlList.join('');
    this.clearHtmlList(); // 초기화
    return snapshot;
  }
  setTemplateData(key : string, value: string): void{
    this.renderTemplate = this.template.replace(`{{__${key}__}}`, value);
  }
  clearHtmlList():void {
    this.htmlList= [];
  }
}

class Router{
  constructor(){
   const routePath = location.hash;

   window.addEventListener('hashchange', router);

   if (routePath === '') {
     newsFeed();
   } else if (routePath.indexOf('#/page/') >= 0) {
     store.currentPage = Number(routePath.substr(7));
     newsFeed();
   } else {
     newsDetail()
   }
 }
}

class NewsFeedView extends View{
  api : NewsFeedApi;
  feeds : NewsFeed[];
  constructor(containerId:string){
  let template: string = ` 
    <div class="bg-gray-600 min-h-screen">
      <div class="bg-white text-xl">
        <div class="mx-auto px-4">
          <div class="flex justify-between items-center py-6">
            <div class="flex justify-start">
              <h1 class="font-extrabold">Hacker News</h1>
            </div>
            <div class="items-center justify-end">
              <a href="#/page/{{__prev_page__}}" class="text-gray-500">
                Previous
              </a>
              <a href="#/page/{{__next_page__}}" class="text-gray-500 ml-4">
                Next
              </a>
            </div>
          </div> 
        </div>
      </div>
      <div class="p-4 text-2xl text-gray-700">
        {{__news_feed__}}        
      </div>
    </div>
  `;

  super(containerId, template);

  this.api = new NewsFeedApi(NEWS_URL);
  this.feeds = store.feeds;

  if (this.feeds.length === 0) {
    this.feeds = store.feeds =this.api.getData();
    this.makeFeeds();
  }
}
  render() : void{

    for(let i = (store.currentPage - 1) * 10; i < store.currentPage * 10; i++) {
      const{ id, title, comments_count, user, points, time_ago, read }= this.feeds[i];
      this.addHtml(`
        <div class="p-6 ${read ? 'bg-red-500' : 'bg-white'} mt-6 rounded-lg shadow-md transition-colors duration-500 hover:bg-green-100">
          <div class="flex">
            <div class="flex-auto">
              <a href="#/show/${id}">${title}</a>  
            </div>
            <div class="text-center text-sm">
              <div class="w-10 text-white bg-green-300 rounded-lg px-0 py-2">${comments_count}</div>
            </div>
          </div>
          <div class="flex mt-3">
            <div class="grid grid-cols-3 text-sm text-gray-500">
              <div><i class="fas fa-user mr-1"></i>${user}</div>
              <div><i class="fas fa-heart mr-1"></i>${points}</div>
              <div><i class="far fa-clock mr-1"></i>${time_ago}</div>
            </div>  
          </div>
        </div>    
      `);
    }
    this.setTemplateData('news_feed', this.getHtml());
    this.setTemplateData('prev_page', String(store.currentPage > 1 ? store.currentPage - 1 : 1));
    this.setTemplateData('next_page', String(store.currentPage + 1));


    this.updateView();

    }
   makeFeeds(): void{ //인스턴스 객체에 접근할 수 있기 때문에 리턴을 하는 함수로 만들 필요가 없다.
      for (let i = 0; i < this.feeds.length; i++) {
        this.feeds[i].read = false;
      }
    }
}

class NewsDetailView extends View{
  constructor(containerID : string){
    let template = `
      <div class="bg-gray-600 min-h-screen pb-8">
        <div class="bg-white text-xl">
          <div class="mx-auto px-4">
            <div class="flex justify-between items-center py-6">
              <div class="flex justify-start">
                <h1 class="font-extrabold">Hacker News</h1>
              </div>
              <div class="items-center justify-end">
                <a href="#/page/{{__currentPage__}}" class="text-gray-500">
                  <i class="fa fa-times"></i>
                </a>
              </div>
            </div>
          </div>
        </div>

        <div class="h-full border rounded-xl bg-white m-6 p-4 ">
          <h2>{__title__}</h2>
          <div class="text-gray-400 h-20">
          {__comments__}}
          </div>

          {{__comments__}}

        </div>
      </div>
    `;

    super(containerID,template);
  }

  render(){
    const id = location.hash.substr(7);
    const api = new NewsDetailApi(CONTENT_URL.replace('@id', id));
    const newsDetail: NewsDetail = api.getData();

    for(let i=0; i < store.feeds.length; i++) {
      if (store.feeds[i].id === Number(id)) {
        store.feeds[i].read = true;
        break;
      }
    }

   this.setTemplateData('comments', this.makeComment(newsDetail.comments));
   this.setTemplateData('current', String(store.currentPage));
   this.setTemplateData('title',newsDetail.title);
   this.setTemplateData('content',newsDetail.content);

   this.updateView();
  }

  makeComment(comments: NewsComment[]): string {
    for(let i = 0; i < comments.length; i++) {
      const comment: NewsComment = comments[i];

      this.addHtml(`
        <div style="padding-left: ${comment.level * 40}px;" class="mt-4">
          <div class="text-gray-400">
            <i class="fa fa-sort-up mr-2"></i>
            <strong>${comment.user}</strong> ${comment.time_ago}
          </div>
          <p class="text-gray-700">${comment.content}</p>
        </div>      
      `);

      if (comment.comments.length > 0) {
        this.addHtml(this.makeComment(comment.comments));
      }
    }
    return this.getHtml();
  }
}
router();
